import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { register } from '../actions/userAction';


function RegisterScreen(props){
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rePassword, setRePassword] = useState('');
    const userRegister = useSelector(state => state.userRegister);
    const {loading, userInfo,  error} = userRegister;
    const dispatch = useDispatch();

    useEffect(() => {
        if(userInfo){
            props.history.push("/");
        }
        return () =>{
            //
        };
    }, [userInfo]);

   
    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(register(name, email, password));
      }
      return <div className="form">
        <form onSubmit={submitHandler} >
          <ul className="form-container">
            <li>
              <h2>Create Account</h2>
            </li>
            <li>
              {loading && <div>Loading...</div>}
              {error && <div>{error}</div>}
            </li>
            <li>
              <label htmlFor="name">
                Name
              </label><br/>
              <input type="name" name="name" id="name" onChange={(e) => setName(e.target.value)}>
              </input>
            </li>
            <li>
              <label htmlFor="email">
                Email
              </label><br/>
              <input type="email" name="email" id="email" onChange={(e) => setEmail(e.target.value)}>
              </input>
            </li>
            <li>
              <label htmlFor="password">Password</label><br/>
              <input type="password" id="password" name="password" onChange={(e) => setPassword(e.target.value)}>
              </input>
            </li>
            <li>
              <label htmlFor="rePassword">Re-Enter Password</label><br/>
              <input type="password" id="rePassword" name="rePassword" onChange={(e) => setRePassword(e.target.value)}>
              </input>
            </li>
            <li>
              <button type="submit" className="button primary full-width">Register</button>
            </li><br/>
            <li>
              Already have an account?<br/>
              <br/>
              <Link to={'/' ? 'signin' : 'signin?redirect='} className="button secondary full-width" >Create your amazona account</Link>
    
            </li>
    
          </ul>
        </form>
      </div>
}
export default RegisterScreen;
